import React, { useState } from 'react';
import axios from 'axios';

const Timetable = () => {
    const [class1Timetable, setClass1Timetable] = useState({});
    const [class2Timetable, setClass2Timetable] = useState({});
    const [error, setError] = useState(null);

    const generateTimetable = async () => {
        try {
            const response = await axios.post('/generate');
            setClass1Timetable(response.data.class1);
            setClass2Timetable(response.data.class2);
            setError(null);
        } catch (err) {
            console.error('Error generating timetable:', err);
            setError('Failed to generate timetable. Please try again.');
        }
    };

    return (
        <div>
            <h1>Automatic Timetable Scheduler</h1>
            <button onClick={generateTimetable}>Generate Timetable</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div id="timetables">
                <h2>Class 1 Timetable</h2>
                <div id="timetable">
                    {Object.entries(class1Timetable).map(([day, hours]) => (
                        <div key={day} className="day">
                            <h3>{day}</h3>
                            {hours.map((subject, index) => (
                                <div key={index} className="hour">
                                    {subject.subject} ({subject.classroom} - {subject.faculty})
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
                <h2>Class 2 Timetable</h2>
                <div id="timetable">
                    {Object.entries(class2Timetable).map(([day, hours]) => (
                        <div key={day} className="day">
                            <h3>{day}</h3>
                            {hours.map((subject, index) => (
                                <div key={index} className="hour">
                                    {subject.subject} ({subject.classroom} - {subject.faculty})
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Timetable;
